let preview = async ({ name, description, version, links }) =>
  await md(`
  ## ${name}
  - Description: ${description}  
  - Version: ${version}  

  #### Links
  ${links.npm ? `- [npm](${links.npm})` : ""}
  ${links.repository ? `- [repository](${links.repository})` : ""}
