    <div class="flex flex-col justify-center p-4 bg-gray-800 rounded">
        <p class="text-3xl font-semibold font-mono text-center text-white">${downloadSpeed.toFixed(2)}</p>
        <p class="text-2xl font-semibold text-center text-gray-300">Mbps</p>
        <p class="text-center text-gray-400">Download</p>
    </div>

    <div class="flex flex-col justify-center p-4 bg-gray-800 rounded">
        <p class="text-3xl font-semibold font-mono text-center text-white">${uploadSpeed.toFixed(2)}</p>
        <p class="text-2xl font-semibold text-center text-gray-300">Mbps</p>
        <p class="text-center text-gray-400">Upload</p>
    </div>
  </div>
</div>
`,
    { width: 640, height: 170, transparent: true }
  );
};

try {
  let downloadSpeed = 0;
  let uploadSpeed = 0;

  div(progressPanelInfo({ downloadSpeed, uploadSpeed }));

  const testResult: SpeedTestResult = await speedTest({
    acceptLicense: true,
    acceptGdpr: true,
    progress: (progress: any) => {
      if (progress.type === "download") {
        downloadSpeed = progress.download.bandwidth / 131072;
      }
      if (progress.type === "upload") {
        uploadSpeed = progress.upload?.bandwidth / 131072;
      }
      setPanel(progressPanelInfo({ downloadSpeed, uploadSpeed }));
    },
  });

  downloadSpeed = testResult.download.bandwidth / 131072;
  uploadSpeed = testResult.upload.bandwidth / 131072;
  let datacenter = `${testResult.server.name} ${testResult.server.location} - ${testResult.server.country}`;

  showResultsPanel({ datacenter, downloadSpeed, uploadSpeed });
} catch (err: any) {
  console.log(err.message);
}

